using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using Vixen.Module.Effect;

namespace Vixen.Sys {
	// This is what represents an editable command generated by a user.
	// This has no knowledge of the command behavior, IEffect does.  This
	// is an effect with parameter values to give it meaning.
	public class Command {
		public Command(Guid effectId, params object[] parameterValues) {
			Effect = VixenSystem.ModuleManagement.GetEffect(effectId);
			ParameterValues = parameterValues;
		}

		public object[] ParameterValues { get; private set; }
		public IEffectModuleInstance Effect { get; private set; }
	}
}
